mapper.select:
select
  id, tid, uid, team_name, team_avatar_url, user_avatar_url, user_grade, creation_time
from wx_interchange.team_user


        and id = #{id}


        and tid = #{tid}


        and uid = #{uid}


        and team_name = #{teamName}


        and team_avatar_url = #{teamAvatarUrl}


        and user_avatar_url = #{userAvatarUrl}


        and user_grade = #{userGrade}


        and creation_time = #{creationTime}

select tid, uid, team_name, team_avatar_url, user_avatar_url, user_grade, creation_time
from wx_interchange.team_user
where uid = #{uid} and user_grade = #{userGrade}

mapper.update:
update wx_interchange.team_user


        tid = #{tid},


        uid = #{uid},


        and team_name = #{teamName}


        and team_avatar_url = #{teamAvatarUrl}


        user_avatar_url = #{userAvatarUrl},


        user_grade = #{userGrade},








where id = #{id}
