mapper.update:
update wx_interchange.user_info

    # uid无特殊情况禁止更新

    username = #{username}
    session_key = #{sessionKey}
    created_team_counts = #{created_team_counts}
    managed_team_counts = #{managed_team_counts}
    joined_team_counts = #{joinedTeamCounts}

mapper.select:
select uid, username, openid, session_key, created_team_counts, managed_team_counts, joined_team_counts
from wx_interchange.user_info
where uid = #{uid}
