mapper.update:
update wx_interchange.user_info
# uid无特殊情况禁止更新
username = #{username}, created_team_counts = #{createdTeamCounts}, managed_team_counts = #{managedTeamCounts}, joined_team_counts = #{joinedTeamCounts}
where uid = #{uid}

mapper.select:
select uid, username, openid, created_team_counts, managed_team_counts, joined_team_counts
from wx_interchange.user_info
where uid = #{uid}

select uid, username, openid, created_team_counts, managed_team_counts, joined_team_counts
from wx_interchange.user_info
where openid = #{openid}
